//Q2: Add Days to a List

  List days = [];
  days.addAll([
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "sunday"
  ]);
  print(days);
  // Step 1: List of Days

  // Q3: Remove Days from a List

  List<String> allDays = [
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "Sunday"
  ];

  while (allDays.isNotEmpty) {
    String removeDays = allDays.removeLast();
    if (removeDays.isNotEmpty) {
      print("$removeDays removed from days");
    }
  }
  print(" All days hasbeen removed");

  //Q4: Find Smallest and Greatest Number
  List<num> numbers = [9, 3, 20, 40, 50];
  num greatestNumber = numbers.reduce(
      (valueOne, valueTwo) => valueOne > valueTwo ? valueOne : valueTwo);
  print('Greatest Number is $greatestNumber');

  num smallestNumber = numbers.reduce(
      (oldValue, newValue) => oldValue < newValue ? oldValue : newValue);
  print("Smallest Number is $smallestNumber");
}


